import React from "react"
import {
  AdaptivityProvider,
  ConfigProvider,
  useAdaptivity,
  AppRoot,
  SplitLayout,
  SplitCol,
  ViewWidth,
  View,
  Panel,
  PanelHeader,
  Header,
  Group,
  SimpleCell,
  withAdaptivity,
  CellButton,
  PopoutWrapper,
  ModalDismissButton,
  Button,
  Div,
  Title
} from "@vkontakte/vkui";
import "@vkontakte/vkui/dist/vkui.css";


function App() {
  const { viewWidth } = useAdaptivity();
  const [popout, setPopout] = React.useState(null);

  const [todos, setTodos] = React.useState([
    {id: 1, completed: false},
    {id: 2, completed: false},
    {id: 3, completed: false},
    {id: 4, completed: false},
    {id: 5, completed: false},
    {id: 6, completed: false},
    {id: 7, completed: false},
    {id: 8, completed: false}
  ])



    const styles = {
      but: {
        textAlign: 'center',
        align: 'center'
      },
      title: {
        marginTop: '5px',
        marginBottom: '5px',
        textAlign: 'center'
      },
      gr: {
        margin: '0 auto 0 auto'
      }
    }

    function button1 () {

    }

  //return
  return (
    <SplitLayout>
      <SplitCol>
    <View popout={popout} activePanel="popout">
      <Panel id="popout" >
      <Title level="1" weight="bold" style={styles.title}>Бит-фонарик</Title>
        <Group style={styles.gr}>

          {() =>{
            if (todos[0].completed === false) {
            <Div>
              <Button size="l" onClick="button1">1</Button>
            </Div>
          } else {
            <Div>
              <Button size="l" onClick="button1" mode="secondary">1</Button>
            </Div>
          }
          }
          }
          
          <Div>
            <Button size="l" onClick="button2">2</Button>
          </Div>
          <Div>
            <Button size="l">3</Button>
          </Div>
          <Div>
            <Button size="l">4</Button>
          </Div>
          <Div>
            <Button size="l">5</Button>
          </Div>
          <Div>
            <Button size="l">6</Button>
          </Div>
          <Div>
            <Button size="l">7</Button>
          </Div>
          <Div>
            <Button size="l">8</Button>
          </Div>
        </Group>
      </Panel>
    </View>
    </SplitCol>
    </SplitLayout>
  );
}

export default App;
