import React from "react"
import {
  AdaptivityProvider,
  ConfigProvider,
  useAdaptivity,
  AppRoot,
  SplitLayout,
  SplitCol,
  ViewWidth,
  View,
  Panel,
  PanelHeader,
  Header,
  Group,
  SimpleCell,
  withAdaptivity,
  CellButton,
  PopoutWrapper,
  ModalDismissButton,
  Button,
  Div,
  Title,
  Cell,
  Switch
} from "@vkontakte/vkui";
import "@vkontakte/vkui/dist/vkui.css";


function App() {
  const { viewWidth } = useAdaptivity();
  const [popout, setPopout] = React.useState(null);

  const [todos] = React.useState([
    {id: 1, completed: false},
    {id: 2, completed: false},
    {id: 3, completed: false},
    {id: 4, completed: false},
    {id: 5, completed: false},
    {id: 6, completed: false},
    {id: 7, completed: false},
    {id: 8, completed: false}
  ])

    const styles = {
      but: {
        textAlign: 'center',
        align: 'center'
      },
      title: {
        marginTop: '5px',
        marginBottom: '5px',
        textAlign: 'center'
      },
      gr: {
        margin: '0 auto 0 auto'
      }
    }

    function button (int) {
      int = Number(int)
      if (todos[int].completed === false) {
        todos[int].completed = true;
      } else {
        todos[int].completed = false;
      }
      console.log(todos[int].completed)
    }


  //return
  return (
    <SplitLayout>
      <SplitCol>
    <View popout={popout} activePanel="popout">
      <Panel id="popout" >
      <Title level="1" weight="bold" style={styles.title}>Бит-фонарик</Title>
        <Group style={styles.gr}>

        <Cell disabled after={<Switch onClick={button(0)} />}></Cell>
        <Cell disabled after={<Switch onClick={button(1)} />}></Cell>
        <Cell disabled after={<Switch onClick={button(2)} />}></Cell>
        <Cell disabled after={<Switch onClick={button(3)} />}></Cell>
        <Cell disabled after={<Switch onClick={button(4)} />}></Cell>
        <Cell disabled after={<Switch onClick={button(5)} />}></Cell>
        <Cell disabled after={<Switch onClick={button(6)} />}></Cell>
        <Cell disabled after={<Switch onClick={button(7)} />}></Cell>


        </Group>
      </Panel>
    </View>
    </SplitCol>
    </SplitLayout>
  );
}

export default App;
