import React from "react"
import {
  AdaptivityProvider,
  ConfigProvider,
  useAdaptivity,
  AppRoot,
  SplitLayout,
  SplitCol,
  ViewWidth,
  View,
  Panel,
  PanelHeader,
  Header,
  Group,
  SimpleCell,
  withAdaptivity,
  CellButton,
  PopoutWrapper,
  ModalDismissButton,
  Button,
  Div,
  Title
} from "@vkontakte/vkui";
import "@vkontakte/vkui/dist/vkui.css";


function App() {
  const { viewWidth } = useAdaptivity();
  const [popout, setPopout] = React.useState(null);


  
    const styles = {
      but: {
        textAlign: 'center',
        align: 'center'
      },
      title: {
        marginTop: '5px',
        marginBottom: '5px',
        textAlign: 'center'
      },
      gr: {
        margin: '0 auto 0 auto'
      }
    }

  //return
  return (
    <SplitLayout>
      <SplitCol>
    <View popout={popout} activePanel="popout">
      <Panel id="popout" >
      <Title level="1" weight="bold" style={styles.title}>Команда "Уральские монстры"</Title>
        <Group style={styles.gr}>
          <CellButton onClick={player1} style={styles.but}>Максим Терновенко</CellButton>
          <CellButton onClick={player2} style={styles.but}>Артём Киселёв</CellButton>
          <CellButton onClick={player3} style={styles.but}>Дарья Вознюк</CellButton>
          <CellButton onClick={player4} style={styles.but}>Олег Царев</CellButton>
        </Group>
      </Panel>
    </View>
    </SplitCol>
    </SplitLayout>
  );
}

export default App;
